import React from "react";
import { Element, scroller } from "react-scroll";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { MDBCarousel, MDBCarouselCaption, MDBCarouselInner, MDBCarouselItem, MDBView, MDBMask, MDBContainer } from
"mdbreact";
// import BackgroundImg from "../../assets/pictures/company_team.jpg";
import { Button } from "../../components/button";
import { DownArrow } from "../../components/downArrow";
import { Logo } from "../../components/logo";
import { Marginer } from "../../components/marginer";
import { Navbar } from "../../components/navbar";

const TopContainer = styled.div`
  width: 100%;
  height: 100vh;
  padding: 0;


  position: relative;
`;

const CarouselPage = () => {
  return (
    <MDBContainer>
      <MDBCarousel
      activeItem={1}
      length={3}
      showControls={true}
      showIndicators={true}
      className="z-depth-1"
    >
      <MDBCarouselInner>
        <MDBCarouselItem itemId="1">
          <MDBView>
            <img
              className="d-block w-100"
              src="https://mdbootstrap.com/img/Photos/Slides/img%20(68).jpg"
              alt="First slide"
            />
          <MDBMask overlay="black-light" />
          </MDBView>
          <MDBCarouselCaption>
            <h3 className="h3-responsive">Light mask</h3>
            <p>First text</p>
          </MDBCarouselCaption>
        </MDBCarouselItem>
        <MDBCarouselItem itemId="2">
          <MDBView>
            <img
              className="d-block w-100"
              src="https://mdbootstrap.com/img/Photos/Slides/img%20(6).jpg"
              alt="Second slide"
            />
          <MDBMask overlay="black-strong" />
          </MDBView>
          <MDBCarouselCaption>
            <h3 className="h3-responsive">Strong mask</h3>
            <p>Second text</p>
          </MDBCarouselCaption>
        </MDBCarouselItem>
        <MDBCarouselItem itemId="3">
          <MDBView>
            <img
              className="d-block w-100"
              src="https://mdbootstrap.com/img/Photos/Slides/img%20(9).jpg"
              alt="Third slide"
            />
          <MDBMask overlay="black-slight" />
          </MDBView>
          <MDBCarouselCaption>
            <h3 className="h3-responsive">Slight Mast</h3>
            <p>Third text</p>
          </MDBCarouselCaption>
        </MDBCarouselItem>
      </MDBCarouselInner>
    </MDBCarousel>
    </MDBContainer>
  );
}

// const BackgroundFilter = styled.div`
//   width: 100%;
//   height: 100%;
//   background: linear-gradient(to right, #000000e1 , rgba(0, 105, 137, 0.90)), url("https://image.freepik.com/free-photo/tutor-college-student-group-sitting-desk-library-studying-reading-doing-homework_28283-1230.jpg") ;
//   background-repeat: no-repeat;
//   background-size: 100% 100%;

//   display: flex;
//   flex-direction: column;
//   align-items: center;
// `;

const MotivationalText = styled.h1`
  font-size: 50px;
  font-weight: 600;

  line-height: 1.4;

  color: #fff;
  margin: 0;

`;

const DownArrowContainer = styled.div`
  position: absolute;
  bottom: 25px;
  left: 50%;
  transform: translateX(-50%);
`;
const Extratext = styled.div`
  color: white;
`;

export function TopSection(props) {
  const scrollToNextSection = () => {
    scroller.scrollTo("servicesSection", { smooth: true, duration: 1500 });
  };

  return (
    <Element name="topSection">
      <TopContainer>
       <CarouselPage>
          <h1>Conference Website</h1>
          <Logo />
          <Navbar />
          <Marginer direction="vertical" margin="8em" />

          <MotivationalText>Future achievers study from IITians Online</MotivationalText>
          <Marginer direction="vertical" margin="2.5em" />
          <Extratext><i>Your Learning Journey Matters to Us</i></Extratext>

          <Marginer direction="vertical" margin="7em" />


          <DownArrowContainer onClick={scrollToNextSection}>
            <DownArrow />
          </DownArrowContainer>
          </CarouselPage>
        {/* </BackgroundFilter> */}
      </TopContainer>
    </Element>
  );
}
