import React from "react";
import { Element, scroller } from "react-scroll";
import styled from "styled-components";
import { DownArrow } from "../../components/downArrow";
// import { Carousel } from 'react-responsive-carousel';
// import "react-responsive-carousel/lib/styles/carousel.min.css";
import { MDBCarousel, MDBCarouselCaption, MDBCarouselInner, MDBCarouselItem, MDBView, MDBMask, MDBContainer } from
"mdbreact";
import { Logo } from "../../components/logo";
import { Marginer } from "../../components/marginer";
import { Navbar } from "../../components/navbar";
// import { Link } from "react-router-dom";
// import { Button } from "../../components/button";
// import BackgroundImg from "../../assets/pictures/company_team.jpg";

const TopContainer = styled.div`
  width: 100%;
  height: 200vh;
  padding: 0;

`;



// const BackgroundFilter = styled.div`
//   width: 100%;
//   height: 100%;
//   background: linear-gradient(to right, #000000e1 , rgba(0, 105, 137, 0.90)), url("https://image.freepik.com/free-photo/tutor-college-student-group-sitting-desk-library-studying-reading-doing-homework_28283-1230.jpg") ;
//   background-repeat: no-repeat;
//   background-size: 100% 100%;

//   display: flex;
//   flex-direction: column;
//   align-items: center;
// `;

const MotivationalText = styled.h1`
  font-size: 50px;
  font-weight: 600;

  line-height: 1.4;

  color: #fff;
  margin: 0;

`;

const DownArrowContainer = styled.div`
  position: absolute;
  bottom: -300px;
  left: 50%;
  transform: translateX(-50%);
`;
const Extratext = styled.div`
  color: white;
`;

export function TopSection(props) {
  const scrollToNextSection = () => {
    scroller.scrollTo("servicesSection", { smooth: true, duration: 1500 });
  };

  return (
    <Element name="topSection">
      <TopContainer>
         <h1>Conference Website</h1>
          <Logo />
          <Navbar />

      {/* <Carousel>
                <div>
                    <img src="https://www.extremetech.com/wp-content/uploads/2019/12/SONATA-hero-option1-764A5360-edit.jpg" width="200" height="600"/>

                </div>
                <div>
                    <img src="https://www.extremetech.com/wp-content/uploads/2019/12/SONATA-hero-option1-764A5360-edit.jpg" width="200" height="600"/>

                </div>
                <div>
                    <img src="https://www.extremetech.com/wp-content/uploads/2019/12/SONATA-hero-option1-764A5360-edit.jpg" width="200" height="600"/>

                </div>
            </Carousel> */}
            <MDBContainer>
      <MDBCarousel
        activeItem={1}
        length={3}
        showControls={true}
        showIndicators={true}
        className="z-depth-1"
      >
        <MDBCarouselInner>
          <MDBCarouselItem itemId="1">
            <MDBView>
              <img
                className="d-block w-100"
                src="https://mdbootstrap.com/img/Photos/Slides/img%20(130).jpg"
                alt="First slide"
              />
            </MDBView>
          </MDBCarouselItem>
          <MDBCarouselItem itemId="2">
            <MDBView>
              <img
                className="d-block w-100"
                src="https://mdbootstrap.com/img/Photos/Slides/img%20(129).jpg"
                alt="Second slide"
              />
            </MDBView>
          </MDBCarouselItem>
          <MDBCarouselItem itemId="3">
            <MDBView>
              <img
                className="d-block w-100"
                src="https://mdbootstrap.com/img/Photos/Slides/img%20(70).jpg"
                alt="Third slide"
              />
            </MDBView>
          </MDBCarouselItem>
        </MDBCarouselInner>
      </MDBCarousel>
    </MDBContainer>


          <MotivationalText>Future achievers study from IITians Online</MotivationalText>
          <Marginer direction="vertical" margin="2.5em" />
          <Extratext><i>Your Learning Journey Matters to Us</i></Extratext>




          <DownArrowContainer onClick={scrollToNextSection}>
            <DownArrow />
          </DownArrowContainer>


      </TopContainer>
    </Element>
  );
}
