import React from "react";
import styled from "styled-components";
import { BrowserRouter, Route, Link } from "react-router-dom";
import { MoreAboutSection } from "../containers/homepage/moreAboutSection";
// import { ReviewsSection } from "../containers/homepage/reviewsSection"
// import { ServicesSection } from "../containers/homepage/servicesSection";
// import { TopSection } from "../containers/homepage/topSection";

const NavLinksContainer = styled.div`
  height: 100%;
  display: flex;
  align-items: center;


`;

const LinksWrapper = styled.ul`
  margin: 0;
  padding: 0;
  display: flex;
  height: 100%;
  list-style: none;

`;

const LinkItem = styled.li`
  height: 100%;
  padding: 0 1.1em;
  color: black;
  font-weight: 500;
  font-size: 15px;
  letter-spacing:1px;
  font-family:'sans serif';
  align-items: center;
  justify-content: center;
  display: flex;
  border-top: 2px solid transparent;
  transition: all 220ms ease-in-out;
  &:hover {
    border-top: 4px solid #35eefc;;
  }
`;

// const Link = styled.a`
//   text-decoration: none;
//   color: inherit;
//   font-size: inherit;
// `;

export function NavLinks(props) {
  return (
    <NavLinksContainer>
      <LinksWrapper>
        <BrowserRouter>
          <Switch>
            <Route path="/" exact>
              <LinkItem>
                <Link >Home</Link>
              </LinkItem>
              <LinkItem>
                <Link type="button" to="/about">About us</Link>
              </LinkItem>
              <LinkItem>
                <Link type="button" to="/tutors">Our Tutors</Link>
              </LinkItem>
              <LinkItem>
                <Link type="button" to="/courses">Buy a Course</Link>
              </LinkItem>
              <LinkItem>
                <Link type="button" to="/contact">Contact Us</Link>
              </LinkItem>
            </Route>

            <Route path="/about">
              <MoreAboutSection/>
            </Route>
            <Route path="/tutors">
              <MoreAboutSection/>
            </Route>
            <Route path="/courses">
              <MoreAboutSection/>
            </Route>
            <Route path="/contact">
              <MoreAboutSection/>
            </Route>
          </Switch>
        </BrowserRouter>

        {/*  */}

        {/* <LinkItem>
          <Link >Home</Link>
        </LinkItem>
        <LinkItem>
          <Link to="/about">About us</Link>
        </LinkItem>
        <LinkItem>
          <Link >Our Tutors</Link>
        </LinkItem>
        <LinkItem>
          <Link >Buy a Course</Link>
        </LinkItem>
        <LinkItem>
          <Link >Contact Us</Link>
        </LinkItem> */}

      </LinksWrapper>
    </NavLinksContainer>
  );
}
