import React from "react";
import styled from "styled-components";

const NavLinksContainer = styled.div`
  height: 100%;
  display: flex;
  align-items: center;


`;

const LinksWrapper = styled.ul`
  margin: 0;
  padding: 0;
  display: flex;
  height: 100%;
  list-style: none;

`;

const LinkItem = styled.li`
  height: 100%;
  padding: 0 1.1em;
  color: black;
  font-weight: 500;
  font-size: 14px;
  letter-spacing:1px;
  font-family:'sans serif';
  align-items: center;
  justify-content: center;
  display: flex;
  border-top: 2px solid transparent;
  transition: all 220ms ease-in-out;
  &:hover {
    border-top: 4px solid  background-color: #6adf76;

  }
`;

const Link = styled.a`
  text-decoration: none;
  color: inherit;
  font-size: inherit;
`;

export function NavLinks(props) {
  return (
    <NavLinksContainer>
      <LinksWrapper>
        <LinkItem>
          <Link href="#">Home</Link>
        </LinkItem>
        <LinkItem>
          <Link href="#">About us</Link>
        </LinkItem>
        <LinkItem>
          <Link href="#">Our Tutors</Link>
        </LinkItem>
        <LinkItem>
          <Link href="#">Buy a Course</Link>
        </LinkItem>
        <LinkItem>
          <Link href="#">Contact Us</Link>
        </LinkItem>

      </LinksWrapper>
    </NavLinksContainer>
  );
}
