import React, { useState } from "react";
import styled from "styled-components";
import { Accessibility } from "./accessibility";
import { MenuToggle } from "./menuToggle";

const NavLinksContainer = styled.div`
  height: 100%;
  display: flex;
  align-items: center;
  justify-content-center;

`;

const LinksWrapper = styled.ul`
  margin: 0;
  padding: 0;
  display: flex;
  height: 32%;
  list-style: none;
  background-color: white;
  width: 100vw;
  flex-direction: column;
  position:absolute;
  top: 60px;
  left: 0;
`;

const LinkItem = styled.li`
  width: 100%;
  padding: 0 1.1em;
  color: black;
  font-weight: 500;
  font-size: 16px;
  display: flex;
  margin-bottom: 15px;
`;

const Link = styled.a`
  text-decoration: none;
  color: inherit;
  font-size: inherit;
`;

const Marginer = styled.div`
  height: 1em;
`;

export function MobileNavLinks(props) {
  const [isOpen, setOpen] = useState(false);

  return (
    <NavLinksContainer>
      <MenuToggle isOpen={isOpen} toggle={() => setOpen(!isOpen)} />
      {isOpen && (
        <LinksWrapper>
        <Marginer />
        <LinkItem>
          <Link href="#">Home</Link>
        </LinkItem>
        <LinkItem>
          <Link href="#">About us</Link>
        </LinkItem>
        <LinkItem>
          <Link href="#">Our Tutors</Link>
        </LinkItem>
        <LinkItem>
          <Link href="#">Buy a Course</Link>
        </LinkItem>
        <LinkItem>
          <Link href="#">Contact Us</Link>
        </LinkItem>


          <Accessibility />

        </LinksWrapper>
      )}
    </NavLinksContainer>
  );
}
